const t=document.querySelector(".theme-toggle");if(t!=null){const e=document.documentElement.classList;t.addEventListener("click",function(){e.contains("dark")?(localStorage.setItem("theme","light"),e.remove("dark")):(localStorage.setItem("theme","dark"),e.add("dark"))});const n=document.querySelector(".nav-toggle"),c=document.querySelector(".nav-menu");n.addEventListener("click",function(){c.classList.toggle("hidden")})}
